import type { ComponentProps, HTMLAttributes } from 'react';
import { Badge } from '@/components/ui/badge';
import { cn } from '@/lib/utils';
export type AnnouncementProps = ComponentProps<typeof Badge> & {
  themed?: boolean;
};
export const Announcement = ({
  variant = 'outline',
  themed = false,
  className,
  ...props
}: AnnouncementProps) => (
  <Badge
    className={cn(
      'group max-w-full gap-2 rounded-full bg-background px-3 py-0.5 font-medium shadow-sm transition-all',
      'hover:shadow-md',
      themed && 'announcement-themed border-foreground/5',
      className
    )}
    variant={variant}
    {...props}
  />
);
export type AnnouncementTagProps = HTMLAttributes<HTMLDivElement>;
export const AnnouncementTag = ({
  className,
  ...props
}: AnnouncementTagProps) => (
  <div
    className={cn(
      '-ml-2.5 shrink-0 truncate rounded-full bg-foreground/5 px-2.5 py-1 text-xs',
      'group-[.announcement-themed]:bg-background/60',
      className
    )}
    {...props}
  />
);
export type AnnouncementTitleProps = HTMLAttributes<HTMLDivElement>;
export const AnnouncementTitle = ({
  className,
  ...props
}: AnnouncementTitleProps) => (
  <div
    className={cn('flex items-center gap-1 truncate py-1', className)}
    {...props}
  />
);

'use client';
import type { HTMLAttributes } from 'react';
import type { MarqueeProps as FastMarqueeProps } from 'react-fast-marquee';
import FastMarquee from 'react-fast-marquee';
import { cn } from '@/lib/utils';
export type MarqueeProps = HTMLAttributes<HTMLDivElement>;
export const Marquee = ({ className, ...props }: MarqueeProps) => (
  <div
    className={cn('relative w-full overflow-hidden', className)}
    {...props}
  />
);
export type MarqueeContentProps = FastMarqueeProps;
export const MarqueeContent = ({
  loop = 0,
  autoFill = true,
  pauseOnHover = true,
  ...props
}: MarqueeContentProps) => (
  <FastMarquee
    autoFill={autoFill}
    loop={loop}
    pauseOnHover={pauseOnHover}
    {...props}
  />
);
export type MarqueeFadeProps = HTMLAttributes<HTMLDivElement> & {
  side: 'left' | 'right';
};
export const MarqueeFade = ({
  className,
  side,
  ...props
}: MarqueeFadeProps) => (
  <div
    className={cn(
      'absolute top-0 bottom-0 z-10 h-full w-24 from-background to-transparent',
      side === 'left' ? 'left-0 bg-gradient-to-r' : 'right-0 bg-gradient-to-l',
      className
    )}
    {...props}
  />
);
export type MarqueeItemProps = HTMLAttributes<HTMLDivElement>;
export const MarqueeItem = ({ className, ...props }: MarqueeItemProps) => (
  <div
    className={cn('mx-2 flex-shrink-0 object-contain', className)}
    {...props}
  />
);

'use client';
import {
  MediaControlBar,
  MediaController,
  MediaMuteButton,
  MediaPlayButton,
  MediaSeekBackwardButton,
  MediaSeekForwardButton,
  MediaTimeDisplay,
  MediaTimeRange,
  MediaVolumeRange,
} from 'media-chrome/react';
import type { ComponentProps, CSSProperties } from 'react';
import { cn } from '@/lib/utils';
export type VideoPlayerProps = ComponentProps<typeof MediaController>;
const variables = {
  '--media-primary-color': 'var(--primary)',
  '--media-secondary-color': 'var(--background)',
  '--media-text-color': 'var(--foreground)',
  '--media-background-color': 'var(--background)',
  '--media-control-hover-background': 'var(--accent)',
  '--media-font-family': 'var(--font-sans)',
  '--media-live-button-icon-color': 'var(--muted-foreground)',
  '--media-live-button-indicator-color': 'var(--destructive)',
  '--media-range-track-background': 'var(--border)',
} as CSSProperties;
export const VideoPlayer = ({ style, ...props }: VideoPlayerProps) => (
  <MediaController
    suppressHydrationWarning={true}
    style={{
      ...variables,
      ...style,
    }}
    {...props}
  />
);
export type VideoPlayerControlBarProps = ComponentProps<typeof MediaControlBar>;
export const VideoPlayerControlBar = (props: VideoPlayerControlBarProps) => (
  <MediaControlBar {...props} />
);
export type VideoPlayerTimeRangeProps = ComponentProps<typeof MediaTimeRange>;
export const VideoPlayerTimeRange = ({
  className,
  ...props
}: VideoPlayerTimeRangeProps) => (
  <MediaTimeRange className={cn('p-2.5', className)} {...props} />
);
export type VideoPlayerTimeDisplayProps = ComponentProps<
  typeof MediaTimeDisplay
>;
export const VideoPlayerTimeDisplay = ({
  className,
  ...props
}: VideoPlayerTimeDisplayProps) => (
  <MediaTimeDisplay className={cn('p-2.5', className)} {...props} />
);
export type VideoPlayerVolumeRangeProps = ComponentProps<
  typeof MediaVolumeRange
>;
export const VideoPlayerVolumeRange = ({
  className,
  ...props
}: VideoPlayerVolumeRangeProps) => (
  <MediaVolumeRange className={cn('p-2.5', className)} {...props} />
);
export type VideoPlayerPlayButtonProps = ComponentProps<typeof MediaPlayButton>;
export const VideoPlayerPlayButton = ({
  className,
  ...props
}: VideoPlayerPlayButtonProps) => (
  <MediaPlayButton className={cn('p-2.5', className)} {...props} />
);
export type VideoPlayerSeekBackwardButtonProps = ComponentProps<
  typeof MediaSeekBackwardButton
>;
export const VideoPlayerSeekBackwardButton = ({
  className,
  ...props
}: VideoPlayerSeekBackwardButtonProps) => (
  <MediaSeekBackwardButton className={cn('p-2.5', className)} {...props} />
);
export type VideoPlayerSeekForwardButtonProps = ComponentProps<
  typeof MediaSeekForwardButton
>;
export const VideoPlayerSeekForwardButton = ({
  className,
  ...props
}: VideoPlayerSeekForwardButtonProps) => (
  <MediaSeekForwardButton className={cn('p-2.5', className)} {...props} />
);
export type VideoPlayerMuteButtonProps = ComponentProps<typeof MediaMuteButton>;
export const VideoPlayerMuteButton = ({
  className,
  ...props
}: VideoPlayerMuteButtonProps) => (
  <MediaMuteButton className={cn('p-2.5', className)} {...props} />
);
export type VideoPlayerContentProps = ComponentProps<'video'>;
export const VideoPlayerContent = ({
  className,
  ...props
}: VideoPlayerContentProps) => (
  <video 
    className={cn('mt-0 mb-0', className)} 
    suppressHydrationWarning={true}
    {...props} 
  />
);
